<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of login * * @author Rita Vyas	 */class login extends CI_Controller {    private $jsFiles;    private $cssFiles;    function __construct() {        parent::__construct();        $this->loadJs();    }    private function loadJs() {        $this->jsFiles = array();//        $this->jsFiles[] = "order.js";    }    public function index() {        $parse = array();        if ($this->session->userdata('user_id') != '') {            redirect('home');            exit;        } else {            $parse['company_type'] = $this->common_function->get_company_type('select');            $parse['content'] = $this->parser->parse('login', $parse, true);            $parse['jsFiles'] = $this->jsFiles;            $parse['failure_msg'] = $this->lib_message->get_failure_message();            $this->parser->parse('common/login-layout', $parse);        }    }    function load_check_user_credential() {        $rec_count = $this->user_modal->check_user_credential($_POST['login_user_email'], $_POST['login_user_password'], 0, 0);        $flag = "success";        if ($rec_count == 0) {            $flag = "fail";        }        echo $flag;        exit();    }    function load_forgot_email() {        $rec_count = $this->user_modal->check_user_credential($_POST['forgot_email'], NULL, 0, 1);        $flag = "success";        if ($rec_count == 0) {            $flag = "fail";        }        echo $flag;        exit();    }    function load_check_user_email() {        $user_login = $this->user_modal->check_register_email($_POST['user_email']);        $flag = "fail";        if ($user_login == 0) {            $flag = "success";        }        echo $flag;        exit();    }    function login_success() {        $user_data = array();        $user_rs = $this->user_modal->check_user_credential($_POST['login_user_email'], $_POST['login_user_password'], 1);        $company_type = $this->user_modal->get_company_type($user_rs->company_id);        $this->session->set_userdata(array(            'user_id' => $user_rs->user_id,            'profile_pic' => $user_rs->profile_pic,            'user_name' => $user_rs->user_name,            'user_email' => $user_rs->user_email,            'user_type' => $user_rs->user_type,            'company_type' => $company_type->company_type,            'company_id' => $user_rs->company_id        ));        redirect('home');        exit;    }    function insert_company() {        $company_name = $_POST['company_name'];        $is_company_exists = $this->user_modal->check_company_exists($company_name);        $long_key = $this->lib_common->generate_long_key();        if ($is_company_exists == 0) {            $user_company_data = array(                'user_name' => $_POST['user_name'],                'user_email' => $_POST['user_email'],                'user_password' => $_POST['user_password'],                'user_type' => $_POST['user_type'],                'company_name' => $_POST['company_name'],                'long_key' => $long_key            );            $this->user_modal->insert_user_company($user_company_data);            redirect('login/company_success');            exit;        } else {            redirect('login/company_error');            exit;        }    }    function company_error() {        $company_error_msg = "<h3>Oops..</h3><br /><br /><span class='error_span'>Your company is already registered. Please contact your companyï¿½s Pangea Panel Admin or email us at</span> <a href='mailto:contact@pangeapanel.com'>contact@pangeapanel.com</a>";        $parse['company_error_msg'] = $company_error_msg;        $parse['content'] = $this->parser->parse('login', $parse, true);        $parse['jsFiles'] = $this->jsFiles;        $this->parser->parse('common/login-layout', $parse);    }    function company_success() {        $user_data = $this->user_modal->get_last_user();        if ($user_data->is_active == '0') {            $validation_link = base_url('login/validate_user/' . $user_data->long_key);            $parse['company_success_msg'] = "<h3>Thanks for Registering</h3><br /><br /> We have sent an email to activate your account. Please check your inbox and/or spam/bulk for that email. <br /> Mail :<br />						Hi " . $user_data->user_name . ", <br /><br />								Thank you for registering with Pangea Panel- The World's Online Panels - United. <br/><br/>....						----- This will be in from email template.......<br/> <br/>			<strong>Validation Link :</strong> <span style='word-break: break-all;'>" . $validation_link . "</span>";        } else {            $parse['company_success_msg'] = "<h3>Thanks for Registration</h3><br /><br /> You have already activate.<br />";        }        $parse['content'] = $this->parser->parse('login', $parse, true);        $parse['jsFiles'] = $this->jsFiles;        $this->parser->parse('common/login-layout', $parse);    }    function validate_user($long_key) {        $user_validate = $this->user_modal->check_long_key($long_key);        if ($user_validate == 0) {            $validation_reg_error_msg = "This link did not work. Please recheck the link. If you are still having problems, please contact us at <a href='mailto:contact@pangeapanel.com'>contact@pangeapanel.com</a>";            $parse['validation_reg_error_msg'] = $validation_reg_error_msg;            $parse['content'] = $this->parser->parse('login', $parse, true);            $this->parser->parse('common/login-layout', $parse);        } else {            $user_data = $this->user_modal->active_user($long_key);            redirect();            exit;        }    }    function forgot_password_process() {        $email_detail = $this->user_modal->check_user_credential($_POST['forgot_email'], NULL, 1, 1);        $new_password = $this->lib_common->random_password();        $forgot_pwd_msg = "If your email address is registered, your new temporary password will be sent to " . $_POST['forgot_email'];        // Email  will be sent  to new email address.        $forgot_pwd_msg.="Hi " . $email_detail->user_name . " <br /> Your New Password is : " . $new_password;        $parse['forgot_pwd_msg'] = $forgot_pwd_msg;        $user_detail = array();        $user_detail['user_password'] = md5($new_password);        $rows = $this->user_modal->update_user_detail($email_detail->user_id, $user_detail);        $parse['content'] = $this->parser->parse('login', $parse, true);        $this->parser->parse('common/login-layout', $parse);        //redirect();        //exit;    }    function logout() {        $this->session->unset_userdata('user_id');        $this->session->unset_userdata('user_name');        $this->session->unset_userdata('user_email');        $this->session->unset_userdata('user_type');        $this->session->unset_userdata('company_id');        redirect();        exit;    }}