lass="form-control placeholder-no-fix" type="password" placeholder="Password" name="user_password" id="user_password" autocomplete="off">			</div>			<span id="user_password_error" class="error_span"></span>		</div>		<div class="form-group">			<label class="control-label visible-ie8 visible-ie9">Confirm Password</label>			<div class="controls">				<div class="input-icon">					<i class="fa fa-check"></i>					<input class="form-control placeholder-no-fix" type="password" placeholder="Confirm Password" name="user_confirm_password" id="user_confirm_password" autocomplete="off">				</div><span id="user_confirm_password_error" class="error_span"></span>			</div>		</div>		<div class="form-group">			<label class="control-label visible-ie8 visible-ie9">Type : </label>			<select class="form-control" name="user_type" id="user_type" ><?php echo $company_type; ?></select>		</div>		<div class="form-group">			<label>			<input type="checkbox" name="tnc" id="tnc" /> I agree to the <a href="#">Terms of Service</a> and <a href="#">Privacy Policy</a>			</label>			<span id="register_tnc_error" class="error_span"></span>		</div>		<div class="form-actions">			<button type="button" id="register-back-btn" class="btn"><i class="m-icon-swapleft"></i> Back</button>			<button type="submit" id="register-submit-btn" class="btn btn-success uppercase pull-right">Submit</button>		</div>	</form>	<!-- END Registration FORM -->	<!-- BEGIN FORGOT PASSWORD FORM -->	<form class="forget-form" action="<?php echo base_url('login/forgot_password_process');?>" method="post" id="frm-forgot">		<h3>Forget Password ?</h3>		<p>			 Enter your e-mail address below to reset your password.		</p>		<div class="form-group">			<div class="input-icon">				<i class="fa fa-envelope"></i>				<input class="form-control placeholder-no-fix" type="text" autocomplete="off" placeholder="Email" name="forgot_email" id="forgot_email" />			</div><span id="forgot_email_error" class="error_span"></span>		</div>		<div class="form-actions">			<button type="button" id="back-btn" class="btn">			<i class="m-icon-swapleft"></i> Back </button>			<button type="submit" class="btn green pull-right">			Submit <i class="m-icon-swapright m-icon-white"></i>			</button>		</div>	</form>	<!-- END FORGOT PASSWORD FORM --><script>jQuery(document).ready(function(){//<![CDATA[jQuery('.register-form').hide();//jQuery('.forget-form').hide();jQuery('#register-btn').click(function() {    jQuery('.login-form').hide();   jQuery('.forget-form').hide();    jQuery('.register-form').show();});jQuery('#register-back-btn').click(function() {   jQuery('.login-form').show();   jQuery('.register-form').hide();   jQuery('.forget-form').hide();});jQuery('#back-btn').click(function() {   jQuery('.login-form').show();   jQuery('.register-form').hide();   jQuery('.forget-form').hide();});jQuery('#forget-password').click(function() {	jQuery('.login-form').hide();    jQuery('.register-form').hide();    jQuery('.forget-form').show();});jQuery('#lnk-forget-password').click(function() {	jQuery('.login-form').hide();    jQuery('.register-form').hide();    jQuery('.forget-form').show();});jQuery(".alert-danger .close-icon").click(function() {	jQuery(".alert-danger").removeClass('display-block');	jQuery(".alert-danger").addClass('display-hide');});var frm_forgot = jQuery("#frm-forgot"); var forgot_email = jQuery("#forgot_email");function validate_forgot_email(){		var forgotemailReg = /^([\w-\.]+@([\w-]+\.)+[\w-]{2,4})?$/;		if(forgot_email.val() == "") { 			forgot_email.addClass("error");			jQuery("#forgot_email_error").html("Please enter email");			return false; 		} else if(!forgotemailReg.test(forgot_email.val())) { 			forgot_email.addClass("error");			jQuery("#forgot_email_error").html("Please enter valid email");			return false;		} else {			var forgot_flag = '';			jQuery.ajax({				type: "POST",				url: "<?php echo base_url('login/load_forgot_email'); ?>",				data: "forgot_email=" + forgot_email.val(),				async: false,				success : function(response){					if(response == "success")   {						forgot_email.removeClass("error");						jQuery("#forgot_email_error").html("");						forgot_flag = true;					}  else   {						forgot_email.addClass("error");						jQuery("#forgot_email_error").html("Email does not not exist. Please contact us at <a href='mailto:contact@pangeapanel.com'>contact@pangeapanel.com</a>");						forgot_flag = false;					}				}			});			return forgot_flag;		}	}	forgot_email.blur(validate_forgot_email); 	forgot_email.keyup(validate_forgot_email);		frm_forgot.submit(function(){ 		if(validate_forgot_email()){ 			return true;		}else{			return false;		}		});	var frm_login = jQuery("#frm-login"); var login_user_email = jQuery("#login_user_email"); var login_user_password = jQuery("#login_user_password"); 	function validate_login_user_name(){		if(login_user_email.val() == "" && login_user_password.val()){			jQuery(".alert-danger").removeClass('display-hide');			jQuery(".alert-danger").addClass('display-block');			return false;		} else {			var flag = '';			jQuery.ajax({				type: "POST",				url: "<?php echo base_url('login/load_check_user_credential')?>",				data: "login_user_email=" + login_user_email.val() +"&login_user_password=" + login_user_password.val(),				async: false,				success : function(response){					if(response == "success")   {						jQuery(".alert-danger").removeClass('display-block');						jQuery(".alert-danger").addClass('display-hide');						flag = true;					}  else   {						jQuery(".alert-danger").removeClass('display-hide');						jQuery(".alert-danger").addClass('display-block');						flag = false;					}					}			});			return flag;		}	}	frm_login.submit(function(){ 		if(validate_login_user_name()){ 			return true;		}else{			return false;		}		});				var frm_signup = jQuery("#frm-signup"); 	var company_name = jQuery("#company_name"); 	function validate_company_name(){		if(company_name.val() == ""){			company_name.addClass("error");			jQuery("#company_name_error").html('Please enter Company Name');			return false;		} else {			company_name.removeClass("error");			jQuery("#company_name_error").html('');			return true;		}	}	company_name.blur(validate_company_name); 	 	var user_password = jQuery("#user_password"); 	function validate_user_password(){		if(user_password.val() == ""){			user_password.addClass("error");			jQuery("#user_password_error").html('Please enter Password');			return false;		}else{			user_password.removeClass("error");			jQuery("#user_password_error").html('');			return true;			}	}	user_password.blur(validate_user_password); 	user_password.keyup(validate_user_password);  	var user_name = jQuery("#user_name"); 	function validate_user_name(){		if(user_name.val() == ""){			user_name.addClass("error");			jQuery("#user_name_error").html('Please enter Full Name');			return false;		}else{			user_name.removeClass("error");			jQuery("#user_name_error").html('');			return true;			}	}	user_name.blur(validate_user_name); 	user_name.keyup(validate_user_name);  	var user_tnc = jQuery("#tnc"); 	function validate_user_tnc(){		if (jQuery('#tnc').prop('checked')){			user_tnc.removeClass("error");			jQuery("#register_tnc_error").html('');			return true;		} else {			user_tnc.addClass("error");			jQuery("#register_tnc_error").html('Please agree our Terms and Conditions');			return false;			}	}	user_tnc.change(validate_user_tnc); 	var user_confirm_password = jQuery("#user_confirm_password"); 	function validate_user_confirm_password(){		if(user_confirm_password.val() == "" || user_confirm_password.val() != user_password.val()){			user_confirm_password.addClass("error");			jQuery("#user_confirm_password_error").html('Please confirm your Password');			return false;		} else {			user_confirm_password.removeClass("error");			jQuery("#user_confirm_password_error").html('');			return true;			}	}	user_confirm_password.blur(validate_user_confirm_password); 	user_confirm_password.keyup(validate_user_confirm_password);  	var user_email = jQuery("#user_email"); 	function validate_user_email(){		var emailReg = /^([\w-\.]+@([\w-]+\.)+[\w-]{2,4})?$/;		if(user_email.val() == "") { 			user_email.addClass("error");			jQuery("#user_email_blank_error").show();			jQuery("#user_email_error").hide();			return false; 		} else if(!emailReg.test(user_email.val())) { 			user_email.addClass("error");			jQuery("#user_email_blank_error").show();			jQuery("#user_email_error").hide();			return false;		} else {			var login_flag = '';			jQuery.ajax({				type: "POST",				url: "<?php echo base_url('login/load_check_user_email'); ?>",				data: "user_email=" + user_email.val(),				async: false,				success : function(response){					if(response == "success")   {						user_email.removeClass("error");						jQuery("#user_email_blank_error").hide();						jQuery("#user_email_error").hide();						login_flag = true;					}  else   {						user_email.addClass("error");						jQuery("#user_email_blank_error").hide();						jQuery("#user_email_error").show();						login_flag = false;					}					//console.log(response);				}			});			return login_flag;		}	}	user_email.blur(validate_user_email); 	user_email.keyup(validate_user_email);		frm_signup.submit(function(){ 		if(validate_company_name() & validate_user_name() & validate_user_email() & validate_user_password() & validate_user_confirm_password() & validate_user_tnc()){ 			return true;		}else{			return false;		}		});		}); //]]></script><?php } ?>